import { Component, OnInit, Input } from '@angular/core';
import { PokemonModel } from '../model/pokemon-model.model';

import { MatDialog } from '@angular/material/dialog';
import { PopUpComponent } from '../pop-up/pop-up/pop-up.component';

@Component({
  selector: 'app-player',
  templateUrl: './player.component.html',
  styleUrls: ['./player.component.scss']
})
export class PlayerComponent implements OnInit{
  constructor( private dialogRef: MatDialog
  ) { }
  @Input() myPokemons: PokemonModel[] = []
  movesAllReady = false

  battleReady() {
    this.dialogRef.open(PopUpComponent)
  }

  modifyMove(idMove: number, idPokemon: number, str: string) {
    console.log(idMove, idPokemon, str)
    let indexPokemon = this.myPokemons.findIndex(pokemon => pokemon.id === idPokemon)
    let myPokemon = this.myPokemons[indexPokemon]
    let indexMove

    if (str === 'add') {
      indexMove = this.myPokemons[indexPokemon].dbMoves.findIndex(move => move.id === idMove)
      myPokemon.moves.push(myPokemon.dbMoves[indexMove])
      myPokemon.dbMoves.splice(indexMove, 1)
    } else if (str === 'subtract') {
      indexMove = this.myPokemons[indexPokemon].moves.findIndex(move => move.id === idMove)
      myPokemon.dbMoves.push(myPokemon.moves[indexMove])
      myPokemon.moves.splice(indexMove, 1)
    }

    if (myPokemon.moves.length == 4) {
      const firstPokemon = this.myPokemons.shift()
      if(firstPokemon){
        this.myPokemons.push(firstPokemon)
      }
    }

    this.allMovesReady()
  }

  allMovesReady(){
    let checker = this.myPokemons.filter(pokemon => pokemon.moves.length < 4)
    if (checker.length == 0) {
      this.movesAllReady = true
    }
  }

  ngOnInit(): void {}
}

<h1>PLAYER</h1>
<div *ngIf="movesAllReady;else movesNotReady">
  <div *ngFor="let pokemon of myPokemons"></div>
</div>
<ng-template #movesNotReady> 
  <div *ngFor="let pokemon of myPokemons">
    <div>
      <img alt="" [src]="pokemon.front_image"/>
      <div *ngFor="let stat of pokemon.stats">
        {{stat.name}} : {{stat.base_stat}}
      </div>
    </div>
    <div *ngIf="pokemon.moves">
      <span *ngFor="let move of pokemon.moves">
        <button style="background-color: red;" (click)="modifyMove(move.id, pokemon.id,'subtract')">{{move.name}} : {{move.power}}</button>
      </span>
    </div>
    <div *ngIf="pokemon.moves.length<4">
      <div *ngFor="let move of pokemon.dbMoves">
        <button style="background-color: blue;" (click)="modifyMove(move.id, pokemon.id,'add')">{{move.name}} : {{move.power}}</button>
      </div>
    </div>
  </div>
</ng-template>
