import { Component, OnInit } from '@angular/core';
import { PokemonService } from '../_services/pokemon/pokemon.service';
import { PokemonModel, RegionPokemon } from '../model/pokemon-model.model';
import { getStats, getMove, getTypes } from '../helper/pokemon-helper';
import { Pokemon } from '../helper/pokemon.class';
import { MoveModel } from '../model/move-model.model';


@Component({
  selector: 'app-player',
  templateUrl: './player.component.html',
  styleUrls: ['./player.component.scss']
})
export class PlayerComponent implements OnInit{
  constructor(
    private http: PokemonService,
    private pokemonService: Pokemon
  ) { }
  // myPokemons: PokemonModel[] = []
  myPokemons: any[] = []
  regionPokemons: RegionPokemon[] = []
  FinalArrMove: MoveModel[] = []
  pokemon: any
  count: number = 0
  isFull: boolean = false


  ngOnInit(): void {
    this.regionPokemons = this.pokemonService.getPokemonRegion()
  }

  async chosenPokemon(id: string){
    this.count++
    this.pokemon = this.http.getPokemon(id).subscribe((data) => {
      this.pokemon = data
      this.myPokemons.push(data)
    })

    if (this.count == 6) {
      let placeHolder = []
      let tempArr = []
      for (const myPokemon of this.myPokemons) {
        let tempArrMove: MoveModel[] = []
        let tempArrStat = []
        for (const moveIndex of myPokemon.moves) {
          let moveDetails = moveIndex.version_group_details[0]
           if (moveDetails.move_learn_method.name === "level-up" &&
              moveDetails.level_learned_at>=15
           ) {
             placeHolder.push(`${moveIndex.move.url}`)
           }
        }
        for (let i = 0; i <= placeHolder.length; i++){
          let tempObjMove
          this.http.getPokemonMove(`${placeHolder[i]}`).subscribe(move => {
            if(move){
              tempObjMove = getMove(move)
              if (tempObjMove.power) {
                tempArrMove.push(tempObjMove)
                this.FinalArrMove.push(tempObjMove)
              }
            }
          })
        }
        // for (const stat of myPokemon.stats) {
        //   let tempObjStat = getStats(stat)
        //   tempArrStat.push(tempObjStat)
        // }

        // let pokemon = {
        //   id: myPokemon.id,
        //   name: myPokemon.name,
        //   stats: tempArrStat,
        //   types: getTypes(myPokemon.types),
        //   moves: tempArrMove,
        //   front_image: myPokemon.sprites.front_default,
        //   back_image: myPokemon.sprites.back_default
        // }
        // tempArr.push(pokemon)

        // if (tempArr.length == this.count) {
        //   console.log(tempArr)
        //   this.myPokemons = tempArr
        // }
      }
    }
  }
}
