import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, catchError, retry } from 'rxjs';
import { PokemonModel, StatsModel } from 'src/app/model/pokemon-model.model';
import { MoveModel } from 'src/app/model/move-model.model';

const POKEMON_API = 'https://pokeapi.co/api/v2/pokemon/';
const kanto = 151

@Injectable({
  providedIn: 'root'
})
export class PokemonService {

  constructor(private http: HttpClient) { }

  getPokemon(id: string): Observable<PokemonModel>{
    return this.http.get<PokemonModel>(`${POKEMON_API}${id}`).pipe(retry(1),catchError(this.handleError))
  }

  getPokemonMove(MOVE_API: string): Observable<any>{
    return this.http.get(`${MOVE_API}`)
  }

  handleError(error: any) {
    let errorMessage = '';
    if (error.error instanceof ErrorEvent) {
      // Get client-side error
      errorMessage = error.error.message;
    } else {
      // Get server-side error
      errorMessage = `Error Code: ${error.status}\nMessage: ${error.message}`;
    }
    window.alert(errorMessage);
    return throwError(() => {
      return errorMessage;
    });
  }
}
