import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { PokemonModel } from '../../model/pokemon-model.model';

const POKEMON_API = 'https://pokeapi.co/api/v2/pokemon/';
const kanto = 151

@Injectable({
  providedIn: 'root'
})
export class PokemonService {

  myPokemon: PokemonModel
  constructor(private http: HttpClient) { }

  getPokemon(id: string): Observable<any>{
    return this.http.get(`${POKEMON_API}${id}`)
  }

  getPokemonMove(MOVE_API: string): Observable<any>{

    return this.http.get(`${MOVE_API}`).Observab(data => {
      let tempMove: any[] = []
        let tempArr = []

        // move to a separate method
        for (const moveIndex of data.moves) {
          let moveDetails = moveIndex.version_group_details[0]
          if (moveDetails.move_learn_method.name === "level-up" &&
              moveDetails.level_learned_at>=15
          )
            this.http.getPokemonMove(`${moveIndex.move.url}`).subscribe(move => {
              let tempObj = getMove(move)
              if(tempObj.power){
                tempMove.push(tempObj)
              }
            })
        }

        for (let i = 0; i < 6; i++){
          let tempObj = getStats(data.stats[i])
          tempArr.push(tempObj)
        }
        //move to a separate method
        const temp = {
          id: data.id,
          name: data.name,
          stats: tempArr,
          types: getTypes(data.types),
          moves: tempMove,
          front_image: data.sprites.front_default,
          back_image: data.sprites.back_default
        }
        this.myPokemon = temp
        console.log(temp)
    })
  }
}
