import { Component, OnInit } from '@angular/core';
import { PokemonService } from '../_services/pokemon/pokemon.service';
import { PokemonModel } from '../model/pokemon-model.model';
import { getStats, getMove, getTypes } from '../helper/pokemon-helper';
import { pok }


@Component({
  selector: 'app-player',
  templateUrl: './player.component.html',
  styleUrls: ['./player.component.scss']
})
export class PlayerComponent implements OnInit{
  constructor(
    private http: PokemonService) { }
  myPokemon: PokemonModel


  ngOnInit(): void {
    // this.pokemon.getPokemonDetails('1')
    this.http.getPokemon('658').subscribe(data => {

      let tempMove: any[] = []
      let tempArr = []

      // move to a separate method
      for (const moveIndex of data.moves) {
        let moveDetails = moveIndex.version_group_details[0]
        if (moveDetails.move_learn_method.name === "level-up" &&
            moveDetails.level_learned_at>=15
        )
          this.http.getPokemonMove(`${moveIndex.move.url}`).subscribe(move => {
            let tempObj = getMove(move)
            if(tempObj.power){
              tempMove.push(tempObj)
            }
          })
      }

      for (let i = 0; i < 6; i++){
        let tempObj = getStats(data.stats[i])
        tempArr.push(tempObj)
      }
      //move to a separate method
      const temp = {
        id: data.id,
        name: data.name,
        stats: tempArr,
        types: getTypes(data.types),
        moves: tempMove,
        front_image: data.sprites.front_default,
        back_image: data.sprites.back_default
      }
      this.myPokemon = temp
      console.log(temp)
    })
  }
}
