import { Injectable } from "@angular/core"
import { PokemonService } from "../_services/pokemon/pokemon.service"
import { PokemonModel } from "../model/pokemon-model.model"
import { getMove, getStats, getTypes
} from "./pokemon-helper"
 import { region } from "./region-helper"


 @Injectable({ providedIn: 'root' })
 export class Pokemon {
  pokemon: PokemonModel
  regionPokemons: PokemonModel[] = [];
   constructor(private http: PokemonService) { }

   getPokemonRegion():any {
     const regionSpecific = 'kanto'
     for (let i = region[regionSpecific].firstPokemon; i <= region[regionSpecific].lastPokemon; i++){
       console.log(this.getPokemonDetails(i.toString()))
       this.regionPokemons.push(this.getPokemonDetails(i.toString()))
     }
     return this.regionPokemons
   }

   async getPokemonDetails(id: string): Promise<any> {
        this.http.getPokemon(id).subscribe(data => {

        let tempMove: any[] = []
        let tempArr = []

        // move to a separate method
        for (const moveIndex of data.moves) {
          let moveDetails = moveIndex.version_group_details[0]
          if (moveDetails.move_learn_method.name === "level-up" &&
              moveDetails.level_learned_at>=15
          )
            this.http.getPokemonMove(`${moveIndex.move.url}`).subscribe(move => {
              let tempObj = getMove(move)
              if(tempObj.power){
                tempMove.push(tempObj)
              }
            })
        }

        for (let i = 0; i < 6; i++){
          let tempObj = getStats(data.stats[i])
          tempArr.push(tempObj)
        }
        //move to a separate method
        this.pokemon = {
          id: data.id,
          name: data.name,
          stats: tempArr,
          types: getTypes(data.types),
          moves: tempMove,
          front_image: data.sprites.front_default,
          back_image: data.sprites.back_default
        }
          return this.pokemon
        })
  }
}
